import React, { useState } from 'react';
import './App.css';
import { Table, Button, Space } from 'antd';
import { EditOutlined, DeleteOutlined } from '@ant-design/icons';

function App() {
    const [dataSource, setDataSource] = useState([
        {
            key: '1',
            dateTime: '10.22.1990',
            name: 'Mike',
            age: 32,
        },
        {
            key: '2',
            dateTime: '10.22.1990',
            name: 'John',
            age: 42,
        },
        {
            key: '3',
            dateTime: '10.22.1990',
            name: 'Bob',
            age: 89,
        },
    ]);

    const handleEdit = (record: any) => {
        console.log('Редактировать:', record);
        // Здесь можно открыть модалку и передать данные record
    };

    const handleDelete = (key: string) => {
        setDataSource((prev) => prev.filter((item) => item.key !== key));
    };

    const columns = [
        {
            title: 'Date',
            dataIndex: 'dateTime',
            key: 'dateTime',
        },
        {
            title: 'Name',
            dataIndex: 'name',
            key: 'name',
        },
        {
            title: 'Age',
            dataIndex: 'age',
            key: 'age',
        },
        {
            title: 'Actions',
            key: 'actions',
            render: (_: any, record: any) => (
                <Space size="middle">
                    <Button
                        type="link"
                        icon={<EditOutlined />}
                        onClick={() => handleEdit(record)}
                    >
                        Edit
                    </Button>
                    <Button
                        type="link"
                        icon={<DeleteOutlined />}
                        danger
                        onClick={() => handleDelete(record.key)}
                    >
                        Delete
                    </Button>
                </Space>
            ),
        },
    ];

    return (
        <div className="App">
            <Table dataSource={dataSource} columns={columns} />
        </div>
    );
}

export default App;
