import React, { useState } from 'react';
import './App.css';
import { Table, Button, Space, Modal, Form, Input } from 'antd';
import { EditOutlined, DeleteOutlined } from '@ant-design/icons';

function App() {
    const [dataSource, setDataSource] = useState([
        { key: '1', dateTime: '1990-10-22', name: 'Mike', age: 32 },
        { key: '2', dateTime: '1990-10-22', name: 'John', age: 42 },
        { key: '3', dateTime: '1990-10-22', name: 'Bob', age: 89 },
    ]);

    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingRecord, setEditingRecord] = useState<UserRecord>();
    const [form] = Form.useForm();

    const handleEdit = (record:any) => {
        setEditingRecord(record);
        form.setFieldsValue(record); // заполняем форму
        setIsModalOpen(true);
    };

    const handleDelete = (key:string) => {
        setDataSource(prev => prev.filter(item => item.key !== key));
    };

    const handleUpdate = () => {
        form.validateFields().then(values => {
            if (editingRecord) {
                // редактируем
                setDataSource(prev =>
                    prev.map(item =>
                        item.key === editingRecord.key ? { ...item, ...values } : item
                    )
                );
            } else {
                // добавляем нового
                const newKey = Date.now().toString(); // простой способ генерации уникального key
                setDataSource(prev => [...prev, { key: newKey, ...values }]);
            }

            setIsModalOpen(false);
            form.resetFields();
            setEditingRecord(undefined);
        });
    };


    type UserRecord = {
        key: string;
        dateTime: string;
        name: string;
        age: number;
    };

    const columns = [
        { title: 'Date', dataIndex: 'dateTime', key: 'dateTime' },
        { title: 'Name', dataIndex: 'name', key: 'name' },
        { title: 'Age', dataIndex: 'age', key: 'age' },
        {
            title: 'Actions',
            key: 'actions',
            render: ( record:UserRecord) => (
                <Space size="middle">
                    <Button
                        type="link"
                        icon={<EditOutlined />}
                        onClick={() => handleEdit(record)}
                    >
                        Edit
                    </Button>
                    <Button
                        type="link"
                        icon={<DeleteOutlined />}
                        danger
                        onClick={() => handleDelete(record.key)}
                    >
                        Delete
                    </Button>
                </Space>
            ),
        },
    ];

    return (
        <div className="App">
            <Button
                type="primary"
                style={{ marginBottom: '16px' }}
                onClick={() => {
                    setEditingRecord(undefined);      // очищаем прошлые данные
                    form.resetFields();              // очищаем форму
                    setIsModalOpen(true);            // открываем модалку
                }}
            >
                Добавить пользователя
            </Button>
            <Table dataSource={dataSource} columns={columns} />

            <Modal
                title="Редактировать пользователя"
                open={isModalOpen}
                onCancel={() => setIsModalOpen(false)}
                onOk={handleUpdate}
                okText="Сохранить"
                cancelText="Отмена"
            >
                <Form form={form} layout="vertical">
                    <Form.Item name="name" label="Имя" rules={[{ required: true }]}>
                        <Input />
                    </Form.Item>
                    <Form.Item name="age" label="Возраст" rules={[{ required: true }]}>
                        <Input />
                    </Form.Item>
                    <Form.Item name="dateTime" label="Дата">
                        <Input  type='date'/>
                    </Form.Item>
                </Form>
            </Modal>
        </div>
    );
}

export default App;
