import React, {useState} from 'react';
import './App.css';
import { Table, Button, Space } from "antd";
import { EditOutlined, DeleteOutlined } from "@ant-design/icons";



function App() {
    const handleEdit = (key:string) => {
        console.log("Редактировать:");
        // Здесь можешь открыть модалку или перейти на страницу редактирования
        dataSource.filter((item)=>{item.key})
    };

    const handleDelete = () => {
        console.log("Удалить:");
        // Можешь использовать setState, чтобы удалить запись из dataSource
    };

    const [dataSource,setDataSource ] = useState(
        [
            {
                key: '1',
                dateTime:'10.22.1990',
                name: 'Mike',
                age: 32,
            },
            {
                key: '2',
                dateTime:'10.22.1990',
                name: 'John',
                age: 42,

            },
            { key: '3',
                dateTime:'10.22.1990',
                name: 'Bob',
                age: 89,},

        ]
    )

    const columns = [
        {
            title: 'Date',
            dataIndex: 'dateTime',
            key: 'dateTime',
        },
        {
            title: 'Name',
            dataIndex: 'name',
            key: 'name',
        },
        {
            title: 'Age',
            dataIndex: 'age',
            key: 'age',
        },
        {
            title: 'Actions',
            key: 'actions',
            render: (key:string) => (
                <Space size="middle">
                    <Button
                        type="link"
                        icon={<EditOutlined />}
                        onClick={() => handleEdit('1')}
                    >
                        Edit
                    </Button>
                    <Button
                        type="link"
                        icon={<DeleteOutlined />}
                        danger
                        onClick={() => handleDelete()}
                    >
                        Delete
                    </Button>
                </Space>
            ),
        },
    ];

    return (
    <div className="App">
<Table dataSource={dataSource} columns={columns}/>
    </div>
  );
}

export default App;
